(1417, 286, '_edit_last', '2'),
(1418, 286, 'position', 'Vice President of Legal Affairs'),
(1419, 286, '_position', 'field_5696e671584fc'),
(1420, 286, 'interesting_quote', 'I chose to be a part of the ZocDoc team because working to transform healthcare through technology, with all of the related legal intricacies and nuances, is an immensely interesting challenge and one I’m excited to help tackle every day.'),
